import React from 'react';

	class Register extends React.Component{

		constructor(props){
			super();
			this.state = {
				registerName: '',
				registerEmail: '',
				registerPassword: ''
			}
		}

		onNameChange = (event) => {
			this.setState({registerName: event.target.value})
		}

		onEmailChange = (event) => {
			console.log('onEmailChange', event.target.value)
			this.setState({registerEmail: event.target.value})
		}

		onPasswordChange = (event) => {
			console.log('password', event.target.value)
			this.setState({registerPassword: event.target.value})
		}

		onSubmitRegister = () => {
			fetch('https://infinite-badlands-96374.herokuapp.com/register', {
				method: 'post',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
					name: this.state.registerName,
					email: this.state.registerEmail,
					password: this.state.registerPassword
				})
			}).then(response => response.json())
			.then(user => {
				if(user.id) {
					this.props.loadUser(user)
					this.props.onRouteChange('home')				
				}

			})
		}


		render(){
			return(
				<article className="br3 ba b--black-10 mv6 shadow-5 w-100 w-50-m w-25-l mw5 center">

				<main className="pa4 black-80">
				  <div className="measure">
				    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
				      <legend className="f1 fw6 ph0 mh0">Register</legend>
		     			<div className="mt3">
				        <label className="db fw6 lh-copy f6" htmlFor="name-address">Name</label>
				        <input 
				        	className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
				        	type="text" 
				        	name="name"  
				        	id="name"
				        	onChange={this.onNameChange}
				        />
				      </div>

				      <div className="mt3">
				        <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
				        <input 
				        	className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
				        	type="email" 
				        	name="email-address"  
				        	id="email-address"
				        	onChange={this.onEmailChange}
				        />
				      </div>
				      <div className="mv3">
				        <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
				        <input 
					        className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
					        type="password" 
					        name="password"  
					        id="password"
   				        	onChange={this.onPasswordChange}
				        />
				      </div>
				    </fieldset>
				    <div className="">
				      <input 
				      onClick={this.onSubmitRegister}
				      className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
				      type="submit" 
				      value="Register"/>
				    </div>
				    <div className="lh-copy mt3">
				    </div>
				  </div>
				</main>
				</article>

			);
		}
	}
export default Register;
